//import Counter from './components/Counter'
import React, { Component } from "react";
import axios from "axios";
//import Weather from './components/Weather'
//import people from './people.json'
import "./App.css";

const dateBuilder = (fecha) => {
  let months = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];
  let days = [
    "Sunday",
    "Monday",
    "Tuesday",
    "Wednesday",
    "Thursday",
    "Friday",
    "Saturday",
  ];
  let day = days[fecha.getDay()];
  let date = fecha.getDate();
  let month = months[fecha.getMonth()];
  let year = fecha.getFullYear();

  return `${day} ${date} ${month}`;
};





class App extends Component {
  state = {
    info: [],

  };


  handleInputChange = (event) => {
    if (event.key === "Enter") {
      const city = event.target.value;
      const key = "58f7999e93e998cd7930c4db09c98aa7";

      const apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${key}`;

      console.log("imputchange event", event.target.value);
      //this.setState({ city: event.target.value });

      axios
        .get(apiUrl)
        .then((res) => {
          console.log("res", res);

          const { data } = res;
          this.setState({ info: data });
        })

        .catch((err) => console.log("err", err));
    }
  };

  render() {
    //   if(typeof this.state.info.main != 'undefined'){
    //   console.log('this.state.info',this.state.info)
    //   console.log('this.state.info.name',this.state.info.name)
    //   console.log('this.state.info.sys.country',this.state.info.sys.country)
    //   console.log('this.state.info.main.humidity', this.state.info.main.humidity)
    //   console.log('this.state.info.main.temp', this.state.info.main.temp)
    //   console.log('this.state.info.weather.main', this.state.info.weather.description)

    // }
    let hour = new Date().getHours();

    console.log("hour", hour);

    return (
      <div className={hour < 20 && hour > 7 ? "app" : "app-night"}>
        <main>
          <div className="search-box">
            <input
              type="text"
              className="search-bar"
              placeholder="search..."
              // onChange={this.handleInputChange}
              onKeyPress={this.handleInputChange}
            />
          </div>
          <div className="location-box">
            <div className="location">{this.state.info.name}</div>
            <div className="date">{dateBuilder(new Date())}</div>
          </div>
          {typeof this.state.info.main != "undefined" ? (
            <div>
              <div className="weather-box">
                <div className="temp">
                  {Math.round(this.state.info.main.temp - 273.15)}ยบ C
                </div>

                <div className="weather">{this.state.info.weather[0].main}</div>
                <div className="pressure">
                  {this.state.info.main.pressure} mbar
                </div>
                <div className="pressure">
                  {this.state.info.main.humidity} % humidity
                </div>
              </div>
            </div>
          ) : (
            <></>
          )}
        </main>
      </div>
    );
  }
}

export default App;
