import React, { Component } from "react";
import axios from "axios";

//const apiUrl = "https://api.openbrewerydb.org/breweries"
const apiUrl = "https://api.openweathermap.org/data/2.5/weather?id=3128760&appid=58f7999e93e998cd7930c4db09c98aa7"
//const apiUrl = "http://api.openweathermap.org/data/2.5/forecast?id=524901&appid=58f7999e93e998cd7930c4db09c98aa7"

class Weather extends Component {

    state= {
        info:[]
    }

  getWeather = () => {
    axios
      .get(apiUrl)
      .then((response) => {
        console.log("response.data", response);
       const { data } = response
       this.setState( { info: data } )
    })

      .catch((err) => console.log("err", err));
  };

  componentDidMount() {
    this.getWeather();
  }

  render() {
      const weatherData = this.state.info
      console.log('state.info', this.state.info)
     // const main = this.state.info.main
     console.log('weatherData.name', weatherData.name)
     // console.log('weatherData.main',weatherData.main)
      //console.log('weatherData.main.humidity',weatherData.main.humidity)
      //console.log('weatherData.main.humidity', weatherData.main.humidity)

    return <div>
      <p>{weatherData.name}</p>
       <p>{weatherData.visibility}</p>



    </div>;
  }
}

export default Weather;


