import React, { Component } from "react";
import axios from "axios";
//import Weather from './components/Weather'
//import people from './people.json'
import "./App.css";

const dateBuilder = (fecha) => {
  let months = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];
  let days = [
    "Monday",
    "Tuesday",
    "Wednesday",
    "Thursday",
    "Friday",
    "Saturday",
    "Sunday",
  ];
  let day = days[fecha.getDay()];
  let date = fecha.getDate();
  let month = months[fecha.getMonth()];
  let year = fecha.getFullYear();

  return `${day} ${date} ${month}`;
};

const apiUrl =
  "https://api.openweathermap.org/data/2.5/weather?id=3128760&appid=58f7999e93e998cd7930c4db09c98aa7";

//const apiUrl = `api.openweathermap.org/data/2.5/weather?q=London&appid=58f7999e93e998cd7930c4db09c98aa7`

class App extends Component {
  state = {
    info: [],

  };

  getWeather = () => {
    axios
      .get(apiUrl)
      .then((res) => {
        console.log("res", res);

        const { data } = res
        this.setState({ info: data });
      })

      .catch((err) => console.log("err", err));
  };

  componentDidMount() {
    this.getWeather();
  }

  handleInputChange = (event) => {
    console.log("event", event.target.value);
    this.setState({ city: event.target.value });
  };

  render() {
    if(typeof this.state.info != 'undefined'){
    console.log('this.state.info',this.state.info)
    console.log('this.state.info.name',this.state.info.name)
    console.log('this.state.info.sys.country',this.state.info.sys.country)
    }


    return (
      <div className="app">
        <main>
          <div className="search-box">
            <input
              type="text"
              className="search-bar"
              placeholder="search..."
              onChange={this.handleInputChange}
            />
          </div>
          <div className="location-box">
            <div className="location">{this.state.info.name}</div>
            <div className="date">{dateBuilder(new Date())}</div>
            <div className="weather-box">
              <div className="temp"> 21ÂºC</div>
              <div className="weather">cloudy</div>
            </div>
          </div>
        </main>
      </div>
    );
  }
}

export default App;
